import random

# sample g' <- Z_N^*, set g <- -(g')^2 mod N


def is_coprime(a, N):
    return gcd(a, N) == 1

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

while True:
    g_prime = random.randint(1, N - 1)
    if is_coprime(g_prime, N):
        break

g = (-pow(g_prime, 2, N)) % N

print(f"g' = {g_prime}")
print(f"g = {g}")